import React from 'react';
import logo from '../images/logo.png';
import banner from '../images/banner.png';

const Header = () => {
  const isSmallScreen = window.innerWidth <= 640;
  return (
    
    <header className="relative h-[1080px]">
      {/* Banner */}
      <div className="absolute inset-0 z-0  bg-cover bg-center" style={{ backgroundImage: `url(${banner})` }}></div>
        <div className="absolute inset-0 bottom-80 flex items-center justify-center">
          <h1 className={`text-white text-center ${isSmallScreen ? 'text-sm' : ''}`}>
            Make Your Interior More Minimalistic & Modern<br />
            <span className={`${isSmallScreen ? 'text-xs' : 'text-sm'}`}>
              Bringing interiors to life, understanding the person or people living in a home is far greater need in design.
            </span>
          </h1>
        </div>
        {/* Header*/}
        <div className="relative z-10 flex border justify-between items-center py-4 px-6 bg-transparent text-white">
          <div>
            <img src={logo} alt="Logo" className="h-10 w-auto" />
          </div>

          {/* Navigation Buttons */}
          <nav className="flex justify-center border flex-grow gap-x-16">
            <button className="text-white text-base hover:text-gray-300">Furniture</button>
            <button className="text-white text-base hover:text-gray-300">Shops</button>
            <button className="text-white text-base hover:text-gray-300">About Us</button>
            <button className="text-white text-base hover:text-gray-300">Contact</button>
          </nav>
      </div>
    </header>
  );
};

export default Header;
