import React from 'react';
import icon1 from '../images/icon.png'
import icon2 from '../images/icon (1).png'
import icon3 from '../images/icon (2).png'
import icon4 from '../images/icon (3).png'

// Sample data for benefits
const benefits = [
  {
    id: 1,
    name: 'Professionalism',
    description: 'We maintain a high level of professionalism in all our dealings.',
    image: icon1,
  },
  {
    id: 2,
    name: 'Smooth workflow',
    description: 'Our workflow is designed to be smooth and efficient for our clients.',
    image: icon2,
  },
  {
    id: 3,
    name: 'Trusted Platform',
    description: 'We are a trusted platform with a proven track record of success.',
    image: icon3,
  },
  {
    id: 4,
    name: 'Qualified employee',
    description: 'Our employees are highly qualified and experienced in their fields.',
    image: icon4,
  },
];

const Benefits = () => {
  return (
    <div className="container mx-auto py-12">
      <h2 className="text-2xl font-bold mb-6">Why Choosing Us</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {benefits.map(benefit => (
          <div key={benefit.id} className="text-left bg-white rounded-lg shadow-md overflow-hidden transition border duration-300 hover:border-glow hover:border-pink-400">
            <img src={benefit.image} alt={benefit.name} className="w-16 h-16 object-cover ml-4 mt-4" />
            <div className="p-4">
              <h3 className="text-lg font-semibold mb-2">{benefit.name}</h3>
              <p className="text-gray-600 mb-2">{benefit.description}</p>
              <a className='text-[#F79489]' href="#">more Info</a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Benefits;
