import React, { useState } from 'react';
import img from '../images/Mask.png'
// Sample data for categories and their descriptions
const categories = [
  { id: 1, name: 'Modern', description: 'Modern design is a term in the design world that refers to a house with a clean design, simple color choices, the use of materials including metal, glass, and steel, and is usually accompanied by clean lines.'},
  { id: 2, name: 'Contemporary', description: 'Description Contemporary' },
  { id: 3, name: 'Minimalist', description: 'Description Minimalist' },
  { id: 4, name: 'Industrial', description: 'Description Industrial' },
];
const HowCanWeHelp = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Function to handle category click
  const handleCategoryClick = (categoryId) => {
    if (selectedCategory === categoryId) {
      setSelectedCategory(null); // Close the category if already open
    } else {
      setSelectedCategory(categoryId); // Open the clicked category
    }
  };
  return (
    <div className="container mx-auto py-12 flex flex-col md:flex-row items-center">
      <div className="w-full border md:w-1/2 text-[#564E4E] md:ml-4 text-left">
        <div>
          <h2 className="text-2xl font-bold mb-6">Visualize your ideas with us</h2>
          <h4>We are ready to help you build and also realize the room design that you dream of, with our experts and also the best category recommendations from us</h4>
        </div>
        {/* Categories */}
        <ul className="space-y-4">
          {categories.map(category => (
            <li key={category.id} className="cursor-pointer">
              <button
                className="text-lg font-semibold text-[#564E4E] focus:outline-none"
                onClick={() => handleCategoryClick(category.id)}
              >
                {category.name}
              </button>
              {/* Display description if the category is selected */}
              {selectedCategory === category.id && (
                <p className="text-gray-600">{category.description}</p>
              )}
            </li>
          ))}
        </ul>
      </div>
      <img src={img} alt="Mask" className="w-1/3 md:w-1/2 object-contain mx-auto md:mr-0 mb-8 md:mb-0" />
    </div>
  );
};

export default HowCanWeHelp;
